package javaFundamentals;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b=100;
		short s=3000;
		int i=10000;
		long l=100000;
		float f=10.9f;
		double d= 34.56;
		char c='C';
		boolean bool=true;
		System.out.println("byte value: "+ b+" Short value:  "+ s+"Integer value:  "+ i+"Long value:  "+ l+"float value:  "+f+"double value:  "+d+"character value: "+ c+"Boolean value:  "+ bool);
	}

}
